mapper.select:
SELECT a.qr_id AS qrId, a.user_no AS userNo, a.check_time AS checkTime, u.no AS userNo, u.id AS userId, u.name AS userName, u.email AS userEmail, u.phone AS userPhone FROM attendance a INNER JOIN users u ON a.user_no = u.no WHERE a.user_no = u.no AND
u.id LIKE CONCAT('%', #{option.keyword}, '%') OR u.name LIKE CONCAT('%', #{option.keyword}, '%') OR u.phone LIKE CONCAT('%', #{option.keyword}, '%')
LIMIT #{page.index}, #{page.rows}

SELECT COUNT(*) FROM attendance a INNER JOIN users u ON a.user_no = u.no
AND u.name LIKE CONCAT('%', #{option.keyword}, '%')
AND a.some_column = #{option.code}

mapper.insert:
        INSERT INTO attendance (qr_id, user_no)
        SELECT #{qrId}, #{userNo}
        WHERE NOT EXISTS (
        SELECT 1
        FROM attendance
        WHERE user_no = #{userNo}
        AND DATE(check_time) = CURDATE()
)

mapper.select:

SELECT COUNT(*)
FROM attendance
WHERE check_time >= DATE_SUB(NOW(), INTERVAL 1 HOUR);
